CREATE  PROCEDURE [dbo].[GetTotalCountOfMedicines]
AS
BEGIN
    SET NOCOUNT ON;

    SELECT SUM(p.rows) 
    FROM sys.partitions p
    WHERE p.object_id = OBJECT_ID(N'[ReactDB].[dbo].[Pharma_Medicines]')
      AND p.index_id IN (0, 1);
END;
