SET ANSI_NULLS ON
GO

SET QUOTED_IDENTIFIER ON
GO

CREATE PROCEDURE [dbo].[GetPaginatedMedicines]
    @PageNumber INT,
    @PageSize INT
AS
BEGIN
    SET NOCOUNT ON;

    SELECT 
        id, name, Batch, price, Expiry, Quantity, QtyInLoose, Is_discontinued, 
        manufacturer_name, type, pack_size_label, short_composition1, 
        short_composition2, UpdatedAt, IsDeleted, Discount, GST
    FROM [ReactDB].[dbo].[Pharma_Medicines]
    ORDER BY name
    OFFSET (@PageNumber - 1) * @PageSize ROWS
    FETCH NEXT @PageSize ROWS ONLY;
END;
